import React, { Component } from 'react';
import './Main.css';
import Axios from 'axios';
import Navbar from '../../components/Navbar/Navbar';
import MyStock from '../../components/MyStock/MyStock';
import HorizontalLine from '../../components/HorizontalLine/HorizontalLine';
import AddStocks from '../../components/AddStocks/AddStocks';

import Modal from './../../common/Modal/Modal';


class Main extends Component {

    state = {
        myStocks: {},
        allStocks: {},
        showModal : false,
        selectedStock : {},
        formComplete : false,
        formIncompleteError : false
    }

    componentDidMount() {
        Axios.get('https://financial-portfolio-trac-178fd.firebaseio.com/allStocks.json')
            .then(response => {
                console.log(response);
                let allStocks = response.data;
                    Axios.get('https://financial-portfolio-trac-178fd.firebaseio.com/myStocks.json')
                    .then(response => {
                        console.log(response);
                    this.setState({
                        myStocks: response.data,
                        allStocks: allStocks
                    })
                })

            })
            .catch(error => {console.log(error)});
    }

    addStockHandler = (stock) => {
        let selectedStock = {};
        selectedStock.symbol = stock.symbol;
        selectedStock.name = stock.name;

        this.setState({
            showModel : true,
            selectedStock : selectedStock
        })
    }

    modalCloseHandler = () => {this.setState({showModel : false})};

    render() {
        let modalContent = this.state.showModel ? 

        (
            <>
            {this.state.formIncompleteError ? <p>Kindly complete the form before adding this stock for tracking</p> : null}
            <div className="AddStockForm">
             <div className="FormInput"><span className="Lable">Company Name:</span><span>{this.state.selectedStock.name}</span></div>
             <div className="FormInput"><span className="Lable">No. of Shares:</span><input id="noShares" type="number" min="1" placeholder="No of Shares" /></div>
             <div className="FormInput"><span className="Lable">Buy Price:</span><input id="buyPrice" type="number" min="1" placeholder="No of Shares" /></div>
             <div className="FormInput"><span className="Lable">Buy Date:</span><input id="buyDate" type="date" /></div>
            </div>
            <button className="AddButton" disabled={this.state.formComplete}>Add</button>
            </>
        )
        :null;

        return (
            <div>
                {
                    this.state.showModel ?
                    <Modal  
                        title={`Add ${this.state.selectedStock.name} to My Stocks`}
                        content = {modalContent}
                        close = {this.modalCloseHandler}/> :
                        null
                }

               <Navbar title="Finance Portfolio Tracker" />
               <MyStock myStocks = {this.state.myStocks}/>
               <HorizontalLine />
               {
                // Object.keys(this.state.myStocks).lenght < 5 ?
               <AddStocks allStocks = {this.state.allStocks} addStock={this.addStockHandler}/> 
            //    <AddStocks error={'You can add only 5 stocks for tracking!'} />
                }
            </div>
        )
    }
}



export default Main;
