                            <td>{stockData.numberOfShares}</td>
                            <td>{stockData.closingPrice}</td>
                            <td>-</td>
                            <td>-</td>
                            <td><button className="StopTrackingbtn">Stop Tracking</button></td>
                        </tr>
                        )})
                        // console.log(rows);
        return (
            <div className="MyStock">
                <div className="Header">
                    <span className="MyStockTitle">My Stocks</span>
                </div>
                <div className="body">
                    <table id="MyStocksTable" className="MyStocksTable">
                        <thead>
                            <tr>
                                <th>Stock symbol</th>
                                <th>Stock name</th>
                                <th>No.of shares</th>
                                <th>Buy price</th>
                                <th>Current price</th>
                                <th>Profit/Loss</th>
                                <th>Stop Tracking</th>
                            </tr>
                        </thead>

                        <tbody>
                            {rows}
                        </tbody>
                    </table>
                </div>

            </div>
        )

}
}
export default MyStock;
